import React from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import { Menu } from "antd";
import styled, { css } from "styled-components";

import logo_image from "../static/img/nexpus_logo_ver2.0_green(40px).png";

const LandongNavigation = ({ children }) => {
  return (
    <div>
      <Root className="root">
        <NavRoot className="navbar_wrapper">
          <Container className="container">
            <LogoWrap href="/" className="logo-text-uppercase">
              <div>
                <LogoImage src={logo_image} alt="Logo"></LogoImage>
              </div>
              <div>
                <p>NextPus</p>
              </div>
            </LogoWrap>
            <ButtonWrap>
              <Link href="/login">
                <Button>로그인</Button>
              </Link>
              <Link href="/signup">
                <SignupButton>회원가입</SignupButton>
              </Link>
            </ButtonWrap>
          </Container>
        </NavRoot>
      </Root>
      <ChildDiv>{children}</ChildDiv>
    </div>
  );
};

const Root = styled.div`
  font-family: sans-serif;
  font-size: 1rem;
  font-weight: bold;
  text-align: left;
`;

const NavRoot = styled.div`
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  background-color: "#f5f9fa";
  max-width: 1128px;
  margin: auto;
  padding: 1
`;

const Container = styled.div`
  width: 90%;
  padding-right: 0px;
  padding-left: 0px;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  box-sizing: border-box;
  @media (min-width: 768px) {
    max-width: 720px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  }
  margin-right: auto;
  margin-left: auto;
  color: "#fff";
`;

const LogoWrap = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
  color: #176b33;
  &:hover {
    color: #176b33;
  }
`;

const LogoImage = styled.img`
  float: left;
  height: 40px;
  &:hover {
    height: 42px;
  }
`;

const ButtonWrap = styled.div`
  width: 400px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 50px auto;
`;

const Button = styled.button`
  width: 96px;
  height: 40px;
  align-items: center;
  text-align: center;
  outline: none;
  padding: 0;
  font-size: 14px;
  font-weight: bold;
  margin: 20px;
  border: 2px solid #209648;
  border-radius: 24px;
  cursor: pointer;
  color: #209648;
  background-color: white;

  &:hover {
    background-color: white;
    color: #176b33;
  }
  & + button {
    margin-left: 1rem;
  }
`;

const SignupButton = styled(Button)`
  border: none;
  color: white;
  background-color: #209648;
  &:hover {
    background-color: #176b33;
    color: white;
  }
`;

const Anchor = styled(Button.withComponent("a"))`
  color: black;
`;

const ChildDiv = styled.div`
  position: relative;
`;

LandongNavigation.propTypes = {
  children: PropTypes.node.isRequired,
};

export default LandongNavigation;
