import { HYDRATE } from "next-redux-wrapper";
import { combineReducers } from "redux";

// import user from "./user";
// import post from "./post";

const initialState = {
  user: {
    isLoggdedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

const login = data => {
  return {
    type: "LOG_IN",
    data,
  };
};

//async action creator(리덕스 사가)

