// store.dispatch(changeNickname("mighty tak"));

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          
        },
      };
  }
};

export default rootReducer;
