import React from "react";
import styled, { css } from "styled-components";
import Link from "next/link";

const LandingPage = () => {
  return (
    <Root>
      <div>Hello intro!</div>
      <div className="first-box_intro-text">
        <p>
          새로운 분야에 도전하거나 직업에 대한 전문성을 키우고 싶은 분들을 위한
        </p>
        <p>학습 종합 플랫폼 -</p>
        <p>NextPus에 오신 걸 환영합니다.</p>
      </div>
      <div className="second-box_business-area">
        <p>Service</p>
        <p>Comming soon</p>
        <p>현재 다양한 서비스를 기획중입니다.</p>
      </div>
      <div className="third-box_explain">
        <p>수많은 매력적인 직업들에 대한 정보를 얻고,</p>
        <p>본인의 전문성 향상 또는 이직준비를 위해 </p>
        <p>관심있는 분야에 대해서 학습할 수 있는 공간입니다.</p>
        <p>직업 사회로의 변환과 산업과 기술환경의 급변함에 맞춰</p>
        <p>
          NextPus를 통해 새로운 전문인으로 본인을 성장시켜나가시기 바랍니다.
        </p>
      </div>
    </Root>
  );

  const RootDiv = styled.div`
    font-family: sans-serif;
    font-size: 1rem;
    font-weight: bold;
    text-align: left;
    background: transparent;
    border: 0;
    font-size: 100%;
    box-sizing: border-box;
  `;

  const NavRoot = styled.div`
    position: relative;
    top: 0;
    right: 0;
    left: 0;
    display: flex;
    flex-wrap: nowrap;
    align-items: center;
    justify-content: space-between;
    background-color: "#f5f9fa";
    max-width: 1128px;
    margin: auto;
    padding: 12px 0 16px;
  `;

  const Container = styled.div`
    width: 90%;
    padding-right: 0px;
    padding-left: 0px;
    display: flex;
    /* flex-wrap: wrap; */
    align-items: center;
    justify-content: space-between;
    box-sizing: border-box;
    /* @media (min-width: 768px) {
    max-width: 720px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  } */
    margin-right: auto;
    margin-left: auto;
    color: "#fff";
  `;

  const LogoWrap = styled.a`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: auto;
    color: #209648;
    &:hover {
      color: #209648;
    }
  `;

  const LogoImage = styled.img`
    float: left;
    height: 40px;
    &:hover {
      height: 42px;
    }
  `;

  const Logo_TextDiv = styled.div`
    text-align: center;
    font-size: 24px;
    margin: auto;
    display: flex;
    align-items: center;
    padding-left: 5px;
  `;

  const ButtonWrap = styled.div`
    /* width: 400px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 50px auto; */
  `;

  const Button = styled.button`
    width: 96px;
    height: 40px;
    align-items: center;
    text-align: center;
    outline: none;
    padding: 0 12px;
    font-size: 14px;
    font-weight: bold;
    margin-left: 0;
    margin-right: 12px;
    border: 2px solid #209648;
    border-radius: 24px;
    cursor: pointer;
    color: #209648;
    background-color: white;
    position: relative;

    &:hover {
      background-color: white;
      color: #176b33;
    }
  `;

  const SignupButton = styled(Button)`
    border: none;
    color: white;
    background-color: #209648;
    &:hover {
      background-color: #176b33;
      color: white;
    }
  `;

  const Anchor = styled(Button.withComponent("a"))`
    color: black;
  `;

  const ChildDiv = styled.div`
    position: relative;
  `;
};

export default LandingPage;
