import React from "react";
import styled, { css } from "styled-components";
import Link from "next/link";
import explain_image from "../static/img/box_intro_01.png";

const LandingPage = () => {
  return (
    <Body>
      <Main className="main">
        <IntroSection>
          <Intro className="first-box_intro-text">
            <SublineText>
              새로운 분야에 도전하거나
              <br></br>
              직업에 대한 전문성을 키우고 싶은 분들을 위한 학습 종합 플랫폼 -
            </SublineText>
            <HeadlineText>
              <b>NextPus</b>에 오신 걸 환영합니다.
            </HeadlineText>
          </Intro>
        </IntroSection>

        {/* 두번째 섹터: 서비스 개요 */}
        <ServiceSection>
          <ServiceDiv>
            <ServiceButtonWrap>
              <h2>Service</h2>
              <ServiceButton>직업, 학습정보 살펴보기</ServiceButton>
              <ServiceButton>전문가의 자기개발</ServiceButton>
              <ServiceButton>학습노하우 및 강의</ServiceButton>
              <ServiceButton>효율적인 학습방법</ServiceButton>
            </ServiceButtonWrap>

            <ServiceCommingButtonWrap>
              <ServiceButtonWrap>
                <div>
                  <h2>Comming Soon</h2>
                </div>
                <ServiceCommingButton>채용정보</ServiceCommingButton>
                <ServiceCommingButton>학습컨텐츠(추가)</ServiceCommingButton>
                <ServiceCommingButton>모임(그룹)</ServiceCommingButton>
                <ServiceCommingButton>뉴스/직무관련 정보</ServiceCommingButton>
              </ServiceButtonWrap>
              <ServiceButtonWrap>
                <ServiceCommingButton>재능판매</ServiceCommingButton>
                <ServiceCommingButton>멘토링/후견인</ServiceCommingButton>
                <ServiceCommingButton>출판플랫폼</ServiceCommingButton>
                <ServiceCommingButton>모바일 서비스</ServiceCommingButton>
              </ServiceButtonWrap>
            </ServiceCommingButtonWrap>
          </ServiceDiv>
          <ServiceText> 현재, 다양한 서비스를 기획중입니다.</ServiceText>
        </ServiceSection>

        {/* 세번째 섹터 */}
        <Section className="third-box_explain">
          <ExplainSection className="explain-section">
            <ExplainDiv className="explain-section__div">
              <SublineText>
                수많은 매력적인 직업들에 대한 정보를 얻고,
                <br></br> 전문지식을 습득하거나, 이직을 위해 관심있는 분야에
                대해서 새롭게 도전해보세요.
              </SublineText>
              <SublineText>
                직업 사회로의 변환, 산업과 기술환경의 급변함에 맞서서 <br></br>
                NextPus를 통해 새로운 전문인으로 본인을 성장시켜나가시기
                바랍니다.
              </SublineText>
            </ExplainDiv>
            <ExplainImg src={explain_image} alt="ExplainImg"></ExplainImg>
          </ExplainSection>
        </Section>
      </Main>
    </Body>
  );
};

const Body = styled.div`
  font-weight: 600;
  font-size: 1.6rem;
  line-height: 1.5;
  color: rgba(0, 0, 0, 0, 9);
  font-family: sans-serif;
  box-sizing: border-box;
  display: block;

  outline: 0;
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  vertical-align: baseline;
  background: transparent;
`;

const Main = styled.div`
  display: flex;
  text-align: left;
  flex-direction: column;
  align-items: center;
  overflow: hidden;
  display: block;
`;

//Main의 기본 contents box
const Section = styled.div`
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  width: 100%;
  max-width: 1128px;
  min-height: 840px;
  padding: 60px, 0;
  position: relative;
`;

// 첫번째 section(intro와 Service 분야)
const IntroSection = styled(Section)`
  align-content: start;
  min-height: 300px;
  padding-bottom: 138px;
  padding-top: 40px;

  top: 0;
  right: 0;
  left: 0;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: space-between;
  background-color: #f5f9fa;
  width: 100%;
  max-width: 1128px;
  margin: auto;
  padding: 52px 0;
`;

const Intro = styled.div`
  width: 100%;
`;

const ServiceSection = styled(Section)`
  align-content: start;
  min-height: 0px;
  padding-bottom: 138px;
  padding-top: 40px;
  top: 0;
  right: 0;
  left: 0;

  flex-wrap: wrap;
  align-items: center;
  background-color: #e8e2d8;
  max-width: 1128px;
  margin: auto;
  padding: 24px 0 36px;
`;

const ServiceDiv = styled.div`
  display: inline-flex;
  flex-direction: row;
  justify-content: center;
  width: 80%;
  margin: auto;
  @media (min-width: 1161px) {
    padding: 28px 0;
  }
`;

const ServiceButtonWrap = styled.div`
  margin-right: 0px;
  padding: 0;
  display: inline-flex;
  flex-grow: 1;
  flex-wrap: wrap;
  width: max-content;
  height: 100%;
  justify-content: flex-end;
  align-items: center;
  flex-direction: column;
