import React from "react";
import styled, { css } from "styled-components";
import Link from "next/link";

const LandingPage = () => {
  return (
    <Body>
      <Main className="main">
        <IntroSection>
          <Intro className="first-box_intro-text">
            <SublineText>
              새로운 분야에 도전하거나
              <br></br>
              직업에 대한 전문성을 키우고 싶은 분들을 위한 학습 종합 플랫폼 -
            </SublineText>
            <HeadlineText>
              <b>NextPus</b>에 오신 걸 환영합니다.
            </HeadlineText>
          </Intro>
        </IntroSection>
        <ServiceSection>
          <div className="second-box_business-area">
            <p>Service</p>
            <ServiceButton>직업, 학습정보 살펴보기</ServiceButton>
            <ServiceButton>전문가의 자기개발</ServiceButton>
            <ServiceButton>학습 노하우 및 강의</ServiceButton>
            <ServiceButton>효율적인 학습방법 보기</ServiceButton>
            <p>Comming soon</p>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <Button>Primary Button</Button>
            <p>현재 다양한 서비스를 기획중입니다.</p>
          </div>
        </ServiceSection>

        <div className="third-box_explain">
          <p>수많은 매력적인 직업들에 대한 정보를 얻고,</p>
          <p>본인의 전문성 향상 또는 이직준비를 위해 </p>
          <p>관심있는 분야에 대해서 학습할 수 있는 공간입니다.</p>
          <p>직업 사회로의 변환과 산업과 기술환경의 급변함에 맞춰</p>
          <p>
            NextPus를 통해 새로운 전문인으로 본인을 성장시켜나가시기 바랍니다.
          </p>
        </div>
      </Main>
    </Body>
  );
};

const Body = styled.div`
  font-weight: 600;
  font-size: 1.6rem;
  line-height: 1.5;
  color: rgba(0, 0, 0, 0, 9);
  font-family: sans-serif;
  box-sizing: border-box;
  display: block;

  outline: 0;
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  vertical-align: baseline;
  background: transparent;
`;

const Main = styled.div`
  display: flex;
  text-align: left;
  flex-direction: column;
  align-items: center;
  overflow: hidden;
  display: block;
`;

//Main의 기본 contents box
const Section = styled.div`
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  width: 100%;
  max-width: 1128px;
  min-height: 840px;
  padding: 60px, 0;
  position: relative;
`;

// 첫번째 section(intro와 Service 분야)
const IntroSection = styled(Section)`
  align-content: start;
  min-height: 300px;
  padding-bottom: 138px;
  padding-top: 40px;

  top: 0;
  right: 0;
  left: 0;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: space-between;
  background-color: #f5f9fa;
  max-width: 1128px;
  margin: auto;
  padding: 12px 0 16px;
`;

const Intro = styled.div`
  width: 100%;
`;

const ServiceSection = styled(Section)`
  align-content: start;
  min-height: 400px;
  padding-bottom: 138px;
  padding-top: 40px;

  top: 0;
  right: 0;
  left: 0;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: space-between;
  background-color: #f7f020;
  max-width: 1128px;
  margin: auto;
  padding: 12px 0 16px;
`;

//헤드라인 텍스트 등 관련
const HeadlineText = styled.h1`
  font-size: 3rem;
  color: #209648;
  font-weight: 300;
  line-height: 70px;

  @media (min-width: 1160px) {
    width: 90%;
    padding-bottom: 24px;
  }
`;

const SublineText = styled(HeadlineText)`
  font-size: 1.2rem;
  color: black;
  font-weight: 200;
  line-height: 35px;
  padding-bottom: 8px;

  @media (min-width: 800px) {
    font-size: 1.5rem;
    width: 90%;
    padding-bottom: 8px;
  }
`;

const Container = styled.div`
  width: 90%;
  padding-right: 0px;
  padding-left: 0px;
  display: flex;
  /* flex-wrap: wrap; */
  align-items: center;
  justify-content: space-between;
  box-sizing: border-box;
  /* @media (min-width: 768px) {
    max-width: 720px;
  }
  @media (min-width: 576px) {
    max-width: 540px;
  } */
  margin-right: auto;
  margin-left: auto;
  color: "#fff";
`;

const LogoWrap = styled.a`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-right: auto;
  color: #209648;
  &:hover {
    color: #209648;
  }
`;

const LogoImage = styled.img`
  float: left;
  height: 40px;
  &:hover {
    height: 42px;
  }
`;

const Logo_TextDiv = styled.div`
  text-align: center;
  font-size: 24px;
  margin: auto;
  display: flex;
  align-items: center;
  padding-left: 5px;
`;

const ButtonWrap = styled.div`
  /* width: 400px;
  display: flex;
  justify-content: center;
