import React, { useState, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import CommentForm from "./CommentForm";
import PostImages from "./PostImages";

import PropTypes from "prop-types";
import { Card, Popover, Button, Avatar, List, Comment } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
  HeartTwoTone,
} from "@ant-design/icons";

// post를 부모로부터 받아옴(main.js)
const PostCard = ({ post }) => {
  const [liked, setLiked] = useState(false);
  const [commentFormOpened, setCommentFormOpened] = useState(false);

  // 좋아요버튼 on/off 기능관련
  const onToggleLike = useCallback(() => {
    setLiked(prev => !prev);
  }, []);
  // 댓글 보이기 on/off 기능관련
  const onToggleComment = useCallback(() => {
    setCommentFormOpened(prev => !prev);
  }, []);
  // 존재여부 확인: 옵셔널 체이닝 연산자 - me.id가 있으면 id로 바꿔주고 아니면 undefined
  const id = useSelector(state => state.user.me && state.user.me.id);

  return (
    <div style={{ marginBottom: 20 }}>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <RetweetOutlined key="retweet" />,
          liked ? (
            <HeartTwoTone
              twoToneColor="#eb2f96"
              key="heart"
              onClick={onToggleLike}
            />
          ) : (
            <HeartOutlined key="heart" onClick={onToggleLike} />
          ),
          <MessageOutlined key="comment" onClick={onToggleComment} />,
          <Popover
            key="more"
            content={
              <Button.Group>
                {/* 내가쓴글권한: 수정/삭제 / 남이쓴 글권한: 신고 */}
                {id && post.User.id === id ? (
                  <>
                    <Button>수정</Button>
                    {/* danger: 붉은색, primary: 파란색 */}
                    <Button type="danger">삭제</Button>
                  </>
                ) : (
                  <Button>신고</Button>
                )}
              </Button.Group>
            }
          >
            <EllipsisOutlined />
          </Popover>,
        ]}
        // extra={<FollowButton post={post} />}
      >
        <Card.Meta
          avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
          title={post.User.nickname}
          description={post.content} />}
        />
        <Image />
        <Content />
        <Buttons></Buttons>
      </Card>
      {/* 댓글 */}
      {commentFormOpened && (
        <div>
          {/* 댓글은 게시글에 종속되어있기 때문에 어떤 게시글인지 정보를 받아야 함 따라서 post={post} */}
          <CommentForm post={post} />
          <List
            header={`${post.Comments.length}개의 댓글`}
            itemLayout="horizonal"
            dataSource={post.Comments}
            // post.comments가 item으로 들어감
            renderItem={item => (
              <li>
                <Comment
                  author={item.User.nickname}
                  avatar={<Avatar>{item.User.nickname[0]}</Avatar>}
                  content={item.content}
                />
              </li>
            )}
          />{" "}
        </div>
      )}

      <Comments />
    </div>
  );
};

PostCard.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
    content: PropTypes.string,
    createdAt: PropTypes.string,
    Comments: PropTypes.arrayOf(PropTypes.object),
    Images: PropTypes.arrayOf(PropTypes.object),
    // Likers: PropTypes.arrayOf(PropTypes.object),
    // RetweetId: PropTypes.number,
    // Retweet: PropTypes.objectOf(PropTypes.any),
  }).isRequired,
};

export default PostCard;

