import React from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import { Menu } from "antd";
import styled, { css } from "styled-components";

import logo_image from "../static/img/nexpus_logo_ver2.0_green(40px).png";

const LandongNavigation = ({ children }) => {
  return (
    <div>
      <div className="root">
        <div className="navbar_wrapper">
          <div className="container">
            <a href="/" className="logo-text-uppercase">
              <LogoImage src={logo_image} alt="Logo"></LogoImage>
              <p>NextPus</p>
            </a>
            <ButtonWrap>
              <Button>로그인</Button>
              <SignupButton>회원가입</SignupButton>
            </ButtonWrap>
          </div>
        </div>
        <div>
          {/* <Menu mode="horizontal">
            <Menu.Item>
              <Link href="/loginpage">
                <a>로그인</a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/profile">
                <a>회원가입</a>
              </Link>
            </Menu.Item>
          </Menu> */}
        </div>
        {children}
      </div>
    </div>
  );
};

const NavRoot = styled.div`
position: fixed;
top: 0;
right: 0;
left: 0;

`

const LogoImage = styled.img`
  height: 60px;
  &:hover {
    height: 65px;
  }
`;

const ButtonWrap = styled.div`
  width: 400px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 50px auto;
`;

const Button = styled.button`
  width: 80px;
  outline: none;
  padding: 15px;
  font-size: 10px;
  font-weight: 100;
  margin: 20px;
  border: none;
  border-radius: 2px;
  cursor: pointer;
  color: ${props => (props.blue ? "black" : "blue")};
  background-color: ${props => (props.blue ? "blue" : "black")};
  
  &:hover {
    background-color: white;
    color: black;
  }
  & + button {
    margin-left: 1rem;
  }
`;

const SignupButton = styled(Button)`
  width: 500px;
  color: yellow;
`;

const Anchor = styled(Button.withComponent("a"))`
  color: black;
`;

LandongNavigation.propTypes = {
  children: PropTypes.node.isRequired,
};

export default LandongNavigation;
