// 대소문자 구분: DB 시퀄라이즈 해당 정보와 다른정보가 합쳐질 경우 대문자로 나옴
// id, content는 post 자체의 속성
// imagePaths: 이미지 업로드시 이미지 경로저장
// postAdded: 게시글 추가가 완료되었을 때 true
export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "전세환",
      },
      content: "첫 번째 게시글 #해시태그 #익스프래스,",
      Images: [
        {
          src:
            "https://newsimg.hankookilbo.com/cms/articlerelease/2020/04/21/202004211422083541_3.jpg",
        },
        {
          src:
            "https://wonderfulmind.co.kr/wp-content/uploads/2018/10/man-on-top-of-books-e1538933803436.jpg",
        },
        {
          src:
            "http://tcpschool.com/lectures/img_deeplearning_07_reinforcement.png",
        },
      ],
      Comments: [
        {
          User: {
            nickname: "nemes",
          },
          content: "우와 개정판이 나왔네요~",
        },
        {
          User: {
            nickname: "hemes",
          },
          content: "좋네욬ㅋㅋ",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default reducer;
