import React, { useState, useCallback } from "react";
import PropTypes from "prop-types";
import Slick from "react-slick";
import styled, { createGlobalStyle } from "styled-components";
import { CloseOutlined } from "@ant-design/icons";

// 확대하기 전체화면으로 채우기 위한 CSS
const Overlay = styled.div`
  position: fixed;
  z-index: 5000;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
`;

const Header = styled.header`
  header: 44px;
  background: white;
  position: relative;
  padding: 0;
  text-align: center;

  /* Header내의 h1컴포넌트 변경 */
  & h1 {
    margin: 0;
    font-size: 17px;
    color: #333;
    line-height: 44px;
  }
`;

const CloseButton = styled(CloseOutlined)`
  position: absolute;
  right: 0;
  top: 0;
  padding: 15px;
  line-height: 14px;
  cursor: pointer;
`;

const SlickWrapper = styled.div`
  height: calc(100% - 44px);
  background: #090909;
`;

const ImageWrapper = styled.div`
  padding: 32px;
  text-align: center;

  & img {
    margin: 0 auto;
    max-height: 750px;
  }
`;

const Indicator = styled.div`
  text-align: center;

  & > div {
    width: 75px;
    height: 30px;
    line-height: 30px;
    border-radius: 15px;
    background: #313131;
    display: inline-block;
    text-align: center;
    color: white;
    font-size: 15px;
  }
`;

// 기본으로 설정되는 라이브러리의 className과 기존 세팅된 css를 변경하기 위한 방법
// (ex. slick-slide는 slick라이브러리에서 이미 정해진 클래스명)
// Global로 할 경우 또한, classname이 변경되지 않고 정한 그대로..
const Global = createGlobalStyle`
.slick-slide {
  display: inline-block;
}
`;

const ImagesZoom = ({ images, onClose }) => {
  // 현재 슬라이드 (번호?)에 대한 state
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleKeyPress = useCallback(e => {
    if (e.key == 27) {
      onClose();
    }
  }, []);

  return (
    <Overlay onKeyDown={handleKeyPress}>
      <Global />
      <Header>
        <h1>상세 이미지</h1>
        <CloseButton onClock={onClose}>X</CloseButton>
      </Header>
      <SlickWrapper>
        <div>
          <Slick
            initialSlide={0}
            // 슬라이드 넘길때 번호 부여
            // arrows: 양옆에 화살표 버튼
            afterChange={slide => setCurrentSlide(slide)}
            infinite
            arrows={false}
            slidesToShow={1}
            slidesToScroll={1}
          >
            {images.map(v => (
              <ImageWrapper key={v.src}>
                <img src={v.src} alt={v.src} />
              </ImageWrapper>
            ))}
          </Slick>
        </div>
      </SlickWrapper>
    </Overlay>
  );
};

ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.object).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ImagesZoom;
